jQuery(function() {
	$('form.track-me').find('input[type="text"],input[type="password"],input[type="email"],select,textarea').each(function() {
		var a = $(this), 
			b = a.parents("form").attr("gaCat") || window.location.href, 
			c = a.attr("gaLabel") || a.attr("placeholder");
		a.bind("focus.formTracker", function() {
			focusTime = Date.parse(new Date) / 1E3;
		});
		a.bind("blur.formTracker", function() {
			blurTime = Date.parse(new Date) / 1E3;
			gaValue = blurTime - focusTime;
			ga('send', 'event', b, "input_exit", c, gaValue);
		});
	});
	$('form.track-me [type="submit"]').each(function() {
		var a = $(this), b = a.parents("form").attr("gaCat") || window.location.href, c = a.attr("gaLabel") || a.text();
		a.bind("click.formTracker", function() {
			ga('send', 'event', b, "submit", c);
		});
	});
});