$('#content .news-paging a').click(function(e) {
e.preventDefault();
var url = $(this).attr("href");
var limit = parseInt($('#content ul.news').attr('data-limit'));
var nodes = parseInt($('#content ul.news').attr('data-nodes'));
var offset = parseInt(url.substr(url.lastIndexOf('/') + 1));
$('#content ul.news li').removeClass('reloaded');
$('<div>').load(url, function() {
$(this).find('li').addClass('reloaded');
$('#content ul.news').append($(this).html());
$('#content ul.news li.reloaded').fadeIn('slow');
if($('#content ul.news li').length < nodes) {
offset += limit;
var linkUrl = url.substr(0, url.lastIndexOf('/') + 1) + offset;
$('#content .news-paging a').attr('href', linkUrl);
}
else {
$('#content .news-paging').hide();
}
});
});
